/**
 *  Light Switch @version v0.1.4
 */

(function () {
  let lightSwitch = document.getElementById('lightSwitch');
  if (!lightSwitch) {
    return;
  }

  /**
   * @function darkmode
   * @summary: changes the theme to 'dark mode' and save settings to local stroage.
   * Basically, replaces/toggles every CSS class that has '-light' class with '-dark'
   */
  function darkMode() {
    document.querySelectorAll('.bg-light').forEach((element) => {
      element.className = element.className.replace(/-light/g, '-dark');
    });

    document.querySelectorAll('.link-dark').forEach((element) => {
      element.className = element.className.replace(/link-dark/, 'text-white');
    });

    document.body.classList.add('bg-dark');

    if (document.body.classList.contains('text-dark')) {
      document.body.classList.replace('text-dark', 'text-light');
    } else {
      document.body.classList.add('text-light');
    }

    // Tables
    var tables = document.querySelectorAll('table');
    for (var i = 0; i < tables.length; i++) {
      // add table-dark class to each table
      tables[i].classList.add('table-dark');
    }

    //Start - Custom modifications for website template
    //navbar menu background color
    document.querySelectorAll(".header-bottom").forEach((element) => {
      element.classList.add('bg-dark');
    });

    //navbar menu options color
    document.querySelectorAll(".nav-link").forEach((element) => {
      element.style.color = "#fcc30d";
    });
    document.querySelectorAll(".nav-link-mobile").forEach((element) => {
      element.style.color = "#fcc30d";
    });

    //navbar menu dark mode logo
    document.querySelector("#web-site-main-logo").src = "assets/images/logo-dark-mode.png";
    document.querySelector(".desktop-logo").style.background = "rgba(255, 255, 255, 0)";

    //navbar mobile menu dark mode logo
    document.querySelector("#web-site-mobile-logo").src = "assets/images/logo-dark-mode.png";
    document.querySelector(".desktop-logo").style.background = "rgba(255, 255, 255, 0)";

    document.querySelector(".bixol-mobile-menu").classList.add("bg-dark");

    //Start - index service section - Title and Description
    document.querySelectorAll(".light-text-color").forEach((element) => {
      element.classList.add("white-text-color");
    });
    document.querySelectorAll(".bright-text-color").forEach((element) => {
      element.classList.add("golden-text-color");
    });
    document.querySelectorAll(".services-column").forEach((element) => {
      element.classList.add("black-background");
    });
    //End - index service section - Title and Description

    //Start - Customer reviews
    if (document.location.pathname == '/index.htm' || document.location.pathname == '/index.html.htm') {
      document.querySelector("#facebook-reviews-dark-mode").classList.remove("invisible-reviews");
      document.querySelector("#facebook-reviews-light-mode").classList.add("invisible-reviews");

      document.querySelector("#google-reviews-dark-mode").classList.remove("invisible-reviews");
      document.querySelector("#google-reviews-light-mode").classList.add("invisible-reviews");
    }
    //End - Customer reviews

    //Start - About us section - Title and Description
    document.querySelectorAll(".about-yr-ex").forEach((element) => {
      element.classList.add("bg-dark");
    });
    document.querySelectorAll(".title").forEach((element) => {
      element.classList.add("light-text-color");
    });
    document.querySelectorAll(".subtitle").forEach((element) => {
      element.classList.add("light-text-color");
    });
    document.querySelectorAll(".service-single-item").forEach((element) => {
      element.classList.add("black-background");
    });
    //End - About us section - Title and Description

    //Start - FAQs section
    document.querySelectorAll(".accordion-item").forEach((element) => {
      element.classList.add("black-background");
    });
    //End - FAQs section

    //Start - Cities Served
    document.querySelectorAll(".blog-content").forEach((element) => {
      element.classList.add("black-background");
    });
    //End - Cities Served

    //footer menu dark mode logo
    document.querySelector("#web-site-footer-logo").src = "assets/images/logo-dark-mode.png";
    document.querySelector(".desktop-logo").style.background = "rgba(255, 255, 255, 0)";

    //Start Contact us Get in touch section
    if (document.location.pathname == '/contact.htm' || document.location.pathname == '/contact.html.htm') {
      document.querySelector(".contact-v2").classList.remove("gray-background");
      document.querySelector(".contact-v2").classList.add("black-background");
    }
    //Start Contact us Get in touch section

    //End - Custom modifications for website template

    // set light switch input to true
    if (!lightSwitch.checked) {
      lightSwitch.checked = true;
    }
    localStorage.setItem('lightSwitch', 'dark');
  }

  /**
   * @function lightmode
   * @summary: changes the theme to 'light mode' and save settings to local stroage.
   */
  function lightMode() {
    document.querySelectorAll('.bg-dark').forEach((element) => {
      element.className = element.className.replace(/-dark/g, '-light');
    });

    document.querySelectorAll('.text-white').forEach((element) => {
      element.className = element.className.replace(/text-white/, 'link-dark');
    });

    document.body.classList.add('bg-light');

    if (document.body.classList.contains('text-light')) {
      document.body.classList.replace('text-light', 'text-dark');
    } else {
      document.body.classList.add('text-dark');
    }

    // Tables
    var tables = document.querySelectorAll('table');
    for (var i = 0; i < tables.length; i++) {
      if (tables[i].classList.contains('table-dark')) {
        tables[i].classList.remove('table-dark');
      }
    }

    //Start - Custom modifications for website template
    //navbar menu background color
    document.querySelectorAll(".header-bottom").forEach((element) => {
      element.classList.remove('bg-light');
      element.classList.add('bg-cyan');
    });

    //navbar menu options text color
    document.querySelectorAll(".nav-link").forEach((element) => {
      element.style.color = "white";
    });

    document.querySelectorAll(".nav-link-mobile").forEach((element) => {
      element.style.color = "#02154e";
    });

    //navbar menu dark mode logo
    document.querySelector("#web-site-main-logo").src = "assets/images/logo.png";
    document.querySelector(".desktop-logo").style.background = "rgba(255, 255, 255, 0)";

    //navbar mobile menu dark mode logo
    document.querySelector("#web-site-mobile-logo").src = "assets/images/logo.png";
    document.querySelector(".desktop-logo").style.background = "rgba(255, 255, 255, 0)";

    document.querySelector(".bixol-mobile-menu").classList.remove("bg-dark");

    //Start - index service section - Title and Description
    document.querySelectorAll(".light-text-color").forEach((element) => {
      element.classList.remove("white-text-color");
    });
    document.querySelectorAll(".bright-text-color").forEach((element) => {
      element.classList.remove("golden-text-color");
    });
    document.querySelectorAll(".services-column").forEach((element) => {
      element.classList.remove("black-background");
    });
    //End - index service section - Title and Description

    //Start - Customer reviews
    if (document.location.pathname == '/index.htm' || document.location.pathname == '/index.html.htm') {
      document.querySelector("#facebook-reviews-light-mode").classList.remove("invisible-reviews");
      document.querySelector("#facebook-reviews-dark-mode").classList.add("invisible-reviews");

      document.querySelector("#google-reviews-light-mode").classList.remove("invisible-reviews");
      document.querySelector("#google-reviews-dark-mode").classList.add("invisible-reviews");
    }
    //End - Customer reviews

    //Start - About us section - Title and Description
    document.querySelectorAll(".about-yr-ex").forEach((element) => {
      element.classList.remove("bg-dark");
    });
    document.querySelectorAll(".title").forEach((element) => {
      element.classList.add("light-text-color");
    });
    document.querySelectorAll(".subtitle").forEach((element) => {
      element.classList.add("light-text-color");
    });
    document.querySelectorAll(".service-single-item").forEach((element) => {
      element.classList.remove("black-background");
    });
    //End - About us section - Title and Description

    //Start - FAQs section
    document.querySelectorAll(".accordion-item").forEach((element) => {
      element.classList.remove("black-background");
    });
    //End - FAQs section

    //Start - Cities Served
    document.querySelectorAll(".blog-content").forEach((element) => {
      element.classList.remove("black-background");
    });
    //End - Cities Served

    //footer dark mode logo
    document.querySelector("#web-site-footer-logo").src = "assets/images/logo.png";
    document.querySelector(".desktop-logo").style.background = "rgba(255, 255, 255, 0)";

    //Start Contact us Get in touch section
    if (document.location.pathname == '/contact.htm' || document.location.pathname == '/contact.html.htm') {
      document.querySelector(".contact-v2").classList.remove("black-background");
      document.querySelector(".contact-v2").classList.add("gray-background");
    }
    //Start Contact us Get in touch section

    //End - Custom modifications for website template

    if (lightSwitch.checked) {
      lightSwitch.checked = false;
    }
    localStorage.setItem('lightSwitch', 'light');
  }

  /**
   * @function onToggleMode
   * @summary: the event handler attached to the switch. calling @darkMode or @lightMode depending on the checked state.
   */
  function onToggleMode() {
    if (lightSwitch.checked) {
      darkMode();
    } else {
      lightMode();
    }
  }

  /**
   * @function getSystemDefaultTheme
   * @summary: get system default theme by media query
   */
  function getSystemDefaultTheme() {
    const darkThemeMq = window.matchMedia('(prefers-color-scheme: dark)');
    if (darkThemeMq.matches) {
      return 'dark';
    }
    return 'light';
  }

  function setup() {
    var settings = localStorage.getItem('lightSwitch');
    if (settings == null) {
      settings = getSystemDefaultTheme();
    }

    if (settings == 'dark') {
      lightSwitch.checked = true;
    }

    lightSwitch.addEventListener('change', onToggleMode);
    onToggleMode();
  }

  setup();
})();
